mapper.select:
select c_name
from customer
where c_id=#{c_id} and c_pass=#{c_pass}


		select * 
		from customer
		where c_id=#{c_id} and c_pass=#{c_pass}

select c_name
from customer
where c_id=#{c_id} and c_pass=#{c_pass}

mapper.update:
 		update customer
	 		set c_pass = #{c_pass}, c_zipcode = #{c_zipcode}, c_adress = #{c_adress},
	 		c_email = #{c_email}, c_sms = #{c_sms} where c_id = #{c_id}
